import { useState } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Separator } from "@/components/ui/separator";
import { CreditCard, Bitcoin } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface CartItem {
  id: string;
  productId: string;
  name: string;
  price: string;
  quantity: number;
}

interface CheckoutModalProps {
  isOpen: boolean;
  onClose: () => void;
  cartItems: CartItem[];
  total: number;
}

export function CheckoutModal({ isOpen, onClose, cartItems, total }: CheckoutModalProps) {
  const [paymentMethod, setPaymentMethod] = useState("stripe");
  const [isProcessing, setIsProcessing] = useState(false);
  const { toast } = useToast();

  const handlePayment = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsProcessing(true);

    try {
      // Simulate payment processing
      await new Promise(resolve => setTimeout(resolve, 3000));

      toast({
        title: "Payment Successful",
        description: "Check your email for download instructions.",
      });

      onClose();
    } catch (error) {
      toast({
        title: "Payment Failed",
        description: "Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsProcessing(false);
    }
  };

  const processingFee = 2.99;
  const subtotal = total - processingFee;

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto" data-testid="modal-checkout">
        <DialogHeader>
          <DialogTitle>Checkout</DialogTitle>
        </DialogHeader>

        <form onSubmit={handlePayment} className="space-y-6">
          {/* Payment Methods */}
          <div>
            <Label className="text-base font-medium">Payment Method</Label>
            <RadioGroup value={paymentMethod} onValueChange={setPaymentMethod} className="mt-2">
              <div className="flex items-center space-x-2 p-4 border border-border rounded-lg cursor-pointer hover:bg-muted">
                <RadioGroupItem value="stripe" id="stripe" data-testid="radio-stripe" />
                <Label htmlFor="stripe" className="flex items-center cursor-pointer">
                  <CreditCard className="w-5 h-5 mr-2 text-blue-500" />
                  Credit Card (Stripe)
                </Label>
              </div>
              <div className="flex items-center space-x-2 p-4 border border-border rounded-lg cursor-pointer hover:bg-muted">
                <RadioGroupItem value="crypto" id="crypto" data-testid="radio-crypto" />
                <Label htmlFor="crypto" className="flex items-center cursor-pointer">
                  <Bitcoin className="w-5 h-5 mr-2 text-orange-500" />
                  Cryptocurrency
                </Label>
              </div>
            </RadioGroup>
          </div>

          {/* Payment Form */}
          {paymentMethod === "stripe" && (
            <div className="space-y-4">
              <Label className="text-base font-medium">Card Information</Label>
              <div className="grid grid-cols-1 gap-4">
                <div>
                  <Label htmlFor="cardNumber">Card Number</Label>
                  <Input
                    id="cardNumber"
                    placeholder="1234 5678 9012 3456"
                    required
                    data-testid="input-card-number"
                  />
                </div>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="expiry">Expiry Date</Label>
                    <Input
                      id="expiry"
                      placeholder="MM/YY"
                      required
                      data-testid="input-expiry"
                    />
                  </div>
                  <div>
                    <Label htmlFor="cvc">CVC</Label>
                    <Input
                      id="cvc"
                      placeholder="123"
                      required
                      data-testid="input-cvc"
                    />
                  </div>
                </div>
              </div>
            </div>
          )}

          {paymentMethod === "crypto" && (
            <div className="space-y-4">
              <Label className="text-base font-medium">Select Cryptocurrency</Label>
              <div className="grid grid-cols-3 gap-4">
                <Button
                  type="button"
                  variant="outline"
                  className="p-6 h-auto flex flex-col items-center"
                  data-testid="button-bitcoin"
                >
                  <Bitcoin className="w-8 h-8 text-orange-500 mb-2" />
                  <span className="text-sm font-medium">Bitcoin</span>
                </Button>
                <Button
                  type="button"
                  variant="outline"
                  className="p-6 h-auto flex flex-col items-center"
                  data-testid="button-ethereum"
                >
                  <div className="w-8 h-8 bg-blue-500 rounded-full flex items-center justify-center mb-2">
                    <span className="text-white text-xs font-bold">Îž</span>
                  </div>
                  <span className="text-sm font-medium">Ethereum</span>
                </Button>
                <Button
                  type="button"
                  variant="outline"
                  className="p-6 h-auto flex flex-col items-center"
                  data-testid="button-usdc"
                >
                  <div className="w-8 h-8 bg-blue-600 rounded-full flex items-center justify-center mb-2">
                    <span className="text-white text-xs font-bold">$</span>
                  </div>
                  <span className="text-sm font-medium">USDC</span>
                </Button>
              </div>
            </div>
          )}

          {/* Order Summary */}
          <div className="p-4 bg-muted rounded-lg">
            <Label className="text-base font-medium">Order Summary</Label>
            <div className="space-y-2 mt-2">
              <div className="flex justify-between">
                <span className="text-muted-foreground">Subtotal:</span>
                <span className="font-medium" data-testid="text-order-subtotal">
                  ${subtotal.toFixed(2)}
                </span>
              </div>
              <div className="flex justify-between">
                <span className="text-muted-foreground">Processing Fee:</span>
                <span className="font-medium" data-testid="text-order-processing-fee">
                  ${processingFee.toFixed(2)}
                </span>
              </div>
              <Separator />
              <div className="flex justify-between items-center">
                <span className="text-lg font-medium">Total:</span>
                <span className="text-xl font-bold text-accent" data-testid="text-order-total">
                  ${total.toFixed(2)}
                </span>
              </div>
            </div>
          </div>

          {/* Submit Button */}
          <Button
            type="submit"
            className="w-full"
            size="lg"
            disabled={isProcessing}
            data-testid="button-complete-purchase"
          >
            {isProcessing ? (
              <div className="flex items-center">
                <div className="loading-spinner w-5 h-5 mr-2"></div>
                Processing...
              </div>
            ) : (
              "Complete Purchase"
            )}
          </Button>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/Navbar.tsx.content:
import { useState, useEffect } from "react";
import { Link, useLocation } from "wouter";
import { useAuth } from "@/hooks/useAuth";
import { useTheme } from "@/components/ThemeProvider";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Sheet, SheetContent, SheetTrigger } from "@/components/ui/sheet";
import { ShoppingCart } from "./ShoppingCart";
import { useQuery } from "@tanstack/react-query";
import { Gamepad2, Search, Sun, Moon, ShoppingCart as CartIcon, Menu, User, Settings, LogOut } from "lucide-react";

interface CartItem {
  id: string;
  productId: string;
  name: string;
  price: string;
  quantity: number;
  imageUrl?: string;
  platform?: string;
}

export function Navbar() {
  const [location] = useLocation();
  const { user, isAuthenticated } = useAuth();
  const { theme, toggleTheme } = useTheme();
  const [searchQuery, setSearchQuery] = useState("");
  const [cartItems, setCartItems] = useState<CartItem[]>([]);

  // Load cart items from localStorage
  useEffect(() => {
    const savedCart = localStorage.getItem('digitalstore-cart');
    if (savedCart) {
      try {
        setCartItems(JSON.parse(savedCart));
      } catch (e) {
        console.error('Failed to parse saved cart:', e);
      }
    }

    // Listen for cart updates
    const handleStorageChange = (e: StorageEvent) => {
      if (e.key === 'digitalstore-cart' && e.newValue) {
        try {
          setCartItems(JSON.parse(e.newValue));
        } catch (error) {
          console.error('Failed to parse cart update:', error);
        }
      }
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  const { data: categories = [] } = useQuery<any[]>({
    queryKey: ["/api/categories"],
  });

  const cartItemCount = cartItems.reduce((sum, item) => sum + item.quantity, 0);

  const updateCartItem = (id: string, quantity: number) => {
    const updatedItems = cartItems
      .map(item => item.id === id ? { ...item, quantity } : item)
      .filter(item => item.quantity > 0);
    setCartItems(updatedItems);
    localStorage.setItem('digitalstore-cart', JSON.stringify(updatedItems));
  };

  const removeCartItem = (id: string) => {
    const updatedItems = cartItems.filter(item => item.id !== id);
    setCartItems(updatedItems);
    localStorage.setItem('digitalstore-cart', JSON.stringify(updatedItems));
  };

  return (
    <nav className="sticky top-0 z-50 bg-card border-b border-border backdrop-blur-sm bg-opacity-95">
      <div className="container mx-auto px-4 py-3">
        <div className="flex items-center justify-between">
          {/* Logo */}
          <Link href="/" data-testid="link-home">
            <div className="flex items-center space-x-2 cursor-pointer">
              <div className="w-8 h-8 bg-primary rounded-lg flex items-center justify-center">
                <Gamepad2 className="text-primary-foreground w-5 h-5" />
              </div>
              <span className="text-xl font-bold text-foreground">GameStore</span>
            </div>
          </Link>

          {/* Search Bar */}
          <div className="hidden md:flex flex-1 max-w-md mx-8">
            <div className="relative w-full">
              <Input
                type="text"
                placeholder="Search games, software, gift cards..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="w-full pr-10"
                data-testid="input-search"
              />
              <Button
                size="sm"
                variant="ghost"
                className="absolute right-1 top-1/2 transform -translate-y-1/2 h-8 w-8 p-0"
                data-testid="button-search"
              >
                <Search className="h-4 w-4" />
              </Button>
            </div>
          </div>

          {/* Navigation Items */}
          <div className="flex items-center space-x-4">
            {/* Theme Toggle */}
            <Button
              variant="ghost"
              size="icon"
              onClick={toggleTheme}
              data-testid="button-theme-toggle"
            >
              {theme === 'light' ? (
                <Sun className="h-5 w-5" />
              ) : (
                <Moon className="h-5 w-5" />
              )}
            </Button>

            {/* Shopping Cart */}
            <Sheet>
              <SheetTrigger asChild>
                <Button variant="ghost" size="icon" className="relative" data-testid="button-cart">
                  <CartIcon className="h-5 w-5" />
                  {cartItemCount > 0 && (
                    <Badge 
                      variant="destructive" 
                      className="absolute -top-1 -right-1 h-5 w-5 flex items-center justify-center p-0 text-xs"
                      data-testid="badge-cart-count"
                    >
                      {cartItemCount}
                    </Badge>
                  )}
                </Button>
              </SheetTrigger>
              <SheetContent className="w-96">
                <ShoppingCart
                  items={cartItems}
                  onUpdateQuantity={updateCartItem}
                  onRemoveItem={removeCartItem}
                />
              </SheetContent>
            </Sheet>

            {/* User Account / Auth */}
            {isAuthenticated ? (
              <div className="hidden md:flex items-center space-x-2">
                {user?.profileImageUrl ? (
                  <img
                    src={user.profileImageUrl}
                    alt="Profile"
                    className="w-8 h-8 rounded-full object-cover"
                    data-testid="img-profile"
                  />
                ) : (
                  <div className="w-8 h-8 bg-primary rounded-full flex items-center justify-center">
                    <User className="w-4 h-4 text-primary-foreground" />
                  </div>
                )}
                <Link href="/profile" data-testid="link-profile">
                  <Button variant="ghost" size="sm">
                    {user?.firstName || user?.email}
                  </Button>
                </Link>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => window.location.href = '/api/logout'}
                  data-testid="button-logout"
                >
                  Logout
                </Button>
              </div>
            ) : (
              <div className="hidden md:flex items-center space-x-2">
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => window.location.href = '/api/login'}
                  data-testid="button-login"
                >
                  Login
                </Button>
                <Button
                  size="sm"
                  onClick={() => window.location.href = '/api/login'}
                  data-testid="button-signup"
                >
                  Sign Up
                </Button>
              </div>
            )}

            {/* Mobile Menu */}
            <Sheet>
              <SheetTrigger asChild>
                <Button variant="ghost" size="icon" className="md:hidden" data-testid="button-mobile-menu">
                  <Menu className="h-5 w-5" />
                </Button>
              </SheetTrigger>
              <SheetContent>
                <div className="flex flex-col space-y-4 mt-8">
                  <div className="relative">
                    <Input
                      type="text"
                      placeholder="Search..."
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                      data-testid="input-mobile-search"
                    />
                  </div>
                  {!isAuthenticated ? (
                    <>
                      <Button
                        variant="outline"
                        onClick={() => window.location.href = '/api/login'}
                        data-testid="button-mobile-login"
                      >
                        Login
                      </Button>
                      <Button
                        onClick={() => window.location.href = '/api/login'}
                        data-testid="button-mobile-signup"
                      >
                        Sign Up
                      </Button>
                    </>
                  ) : (
                    <>
                      <Link href="/profile" data-testid="link-mobile-profile">
                        <Button variant="outline" className="w-full justify-start">
                          Profile
                        </Button>
                      </Link>
                      <Button
                        variant="outline"
                        onClick={() => window.location.href = '/api/logout'}
                        data-testid="button-mobile-logout"
                      >
                        Logout
                      </Button>
                    </>
                  )}
                </div>
              </SheetContent>
            </Sheet>
          </div>
        </div>

        {/* Categories */}
        <div className="hidden md:flex items-center space-x-8 mt-4 pt-4 border-t border-border">
          <Link href="/?featured=true" data-testid="link-featured">
            <span className={`font-medium transition-colors ${location === '/?featured=true' ? 'text-primary' : 'text-muted-foreground hover:text-foreground'}`}>
              Featured
            </span>
          </Link>
          {categories.map((category: any) => (
            <Link key={category.id} href={`/?category=${category.id}`} data-testid={`link-category-${category.slug}`}>
              <span className={`transition-colors ${location === `/?category=${category.id}` ? 'text-primary' : 'text-muted-foreground hover:text-foreground'}`}>
                {category.name}
              </span>
            </Link>
          ))}
        </div>
      </div>
    </nav>
  );
}

client/src/components/ProductCard.tsx.content:
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Star, Heart, ShoppingCart, Zap } from "lucide-react";
import { ProductWithCategory } from "@shared/schema";
import { useState, useEffect } from "react";
import { useToast } from "@/hooks/use-toast";

interface ProductCardProps {
  product: ProductWithCategory;
  onAddToCart: (product: ProductWithCategory) => void;
}

export function ProductCard({ product, onAddToCart }: ProductCardProps) {
  const [isInWishlist, setIsInWishlist] = useState(false);
  const [isAdding, setIsAdding] = useState(false);
  const { toast } = useToast();

  const hasDiscount = product.originalPrice && parseFloat(product.originalPrice) > parseFloat(product.price);
  const discountPercentage = hasDiscount ? Math.round(((parseFloat(product.originalPrice!) - parseFloat(product.price)) / parseFloat(product.originalPrice!)) * 100) : 0;
  const isLowStock = product.stock && product.stock < 10;

  useEffect(() => {
    const wishlist = JSON.parse(localStorage.getItem('digitalstore-wishlist') || '[]');
    setIsInWishlist(wishlist.some((item: any) => item.productId === product.id));
  }, [product.id]);

  const handleAddToCart = async () => {
    if (product.stock === 0) {
      toast({
        title: "Out of Stock",
        description: "This product is currently out of stock.",
        variant: "destructive",
      });
      return;
    }

    setIsAdding(true);
    onAddToCart(product);

    // Simulate quick loading state
    setTimeout(() => setIsAdding(false), 300);
  };

  const handleWishlistToggle = () => {
    const wishlist = JSON.parse(localStorage.getItem('digitalstore-wishlist') || '[]');

    if (isInWishlist) {
      const newWishlist = wishlist.filter((item: any) => item.productId !== product.id);
      localStorage.setItem('digitalstore-wishlist', JSON.stringify(newWishlist));
      setIsInWishlist(false);
      toast({
        title: "Removed from Wishlist",
        description: `${product.name} has been removed from your wishlist.`,
      });
    } else {
      const newItem = {
        id: `wishlist-${Date.now()}`,
        productId: product.id,
        name: product.name,
        price: product.price,
        imageUrl: product.imageUrl,
        platform: product.platform,
      };
      wishlist.push(newItem);
      localStorage.setItem('digitalstore-wishlist', JSON.stringify(wishlist));
      setIsInWishlist(true);
      toast({
        title: "Added to Wishlist",
        description: `${product.name} has been added to your wishlist.`,
      });
    }
  };

  return (
    <Card className="product-card bg-card rounded-lg overflow-hidden border border-border hover:shadow-lg transition-all duration-300 hover:-translate-y-2 group relative" data-testid={`card-product-${product.id}`}>
      <div className="relative">
        <img
          src={product.imageUrl || "https://images.unsplash.com/photo-1542751371-adc38448a05e?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=225"}
          alt={product.name}
          className="w-full h-48 object-cover"
          data-testid={`img-product-${product.id}`}
        />

        {/* Overlay on hover */}
        <div className="absolute inset-0 bg-black/60 opacity-0 group-hover:opacity-100 transition-opacity duration-300 flex items-center justify-center">
          <div className="flex space-x-2">
            <Button
              size="sm"
              onClick={handleWishlistToggle}
              variant={isInWishlist ? "default" : "secondary"}
              data-testid={`button-wishlist-${product.id}`}
            >
              <Heart className={`w-4 h-4 ${isInWishlist ? 'fill-current' : ''}`} />
            </Button>
            <Button
              size="sm"
              onClick={handleAddToCart}
              disabled={isAdding || product.stock === 0}
              data-testid={`button-quick-add-${product.id}`}
            >
              <ShoppingCart className="w-4 h-4" />
            </Button>
          </div>
        </div>

        {/* Badges */}
        <div className="absolute top-2 left-2 flex flex-col space-y-1">
          {product.isFeatured && (
            <Badge className="bg-yellow-500 text-black" data-testid={`badge-featured-${product.id}`}>
              <Zap className="w-3 h-3 mr-1" />
              Featured
            </Badge>
          )}
          {hasDiscount && (
            <Badge variant="destructive" data-testid={`badge-discount-${product.id}`}>
              -{discountPercentage}%
            </Badge>
          )}
          {isLowStock && product.stock! > 0 && (
            <Badge variant="secondary" className="bg-orange-500 text-white" data-testid={`badge-low-stock-${product.id}`}>
              Only {product.stock} left
            </Badge>
          )}
          {product.stock === 0 && (
            <Badge variant="secondary" className="bg-gray-500 text-white" data-testid={`badge-out-of-stock-${product.id}`}>
              Out of Stock
            </Badge>
          )}
        </div>
      </div>
      <CardContent className="p-4">
        <div className="flex items-center justify-between mb-2">
          <Badge variant="secondary" className="text-xs" data-testid={`badge-category-${product.id}`}>
            {product.category.name}
          </Badge>
          {product.rating && parseFloat(product.rating) > 0 && (
            <div className="flex items-center text-yellow-400">
              <Star className="w-4 h-4 fill-current" />
              <span className="text-xs text-muted-foreground ml-1" data-testid={`text-rating-${product.id}`}>
                {product.rating}
              </span>
            </div>
          )}
        </div>

        <h3 className="font-semibold text-card-foreground mb-2 line-clamp-2" data-testid={`text-title-${product.id}`}>
          {product.name}
        </h3>

        {product.description && (
          <p className="text-muted-foreground text-sm mb-3 line-clamp-2" data-testid={`text-description-${product.id}`}>
            {product.description}
          </p>
        )}

        {product.platform && (
          <p className="text-xs text-muted-foreground mb-2" data-testid={`text-platform-${product.id}`}>
            {product.platform}
          </p>
        )}

        <div className="space-y-3">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              {hasDiscount && (
                <span className="text-sm text-muted-foreground line-through" data-testid={`text-original-price-${product.id}`}>
                  ${product.originalPrice}
                </span>
              )}
              <span className="text-lg font-bold text-accent" data-testid={`text-price-${product.id}`}>
                ${product.price}
              </span>
            </div>

            <Button
              variant="ghost"
              size="sm"
              onClick={handleWishlistToggle}
              className={`p-2 ${isInWishlist ? 'text-red-500' : 'text-muted-foreground'}`}
              data-testid={`button-wishlist-mobile-${product.id}`}
            >
              <Heart className={`w-4 h-4 ${isInWishlist ? 'fill-current' : ''}`} />
            </Button>
          </div>

          <Button
            className="w-full hover:opacity-90 transition-opacity"
            onClick={handleAddToCart}
            disabled={isAdding || product.stock === 0}
            data-testid={`button-add-to-cart-${product.id}`}
          >
            {isAdding ? (
              <div className="flex items-center">
                <div className="animate-spin w-4 h-4 border-2 border-primary border-t-transparent rounded-full mr-2"></div>
                Adding...
              </div>
            ) : product.stock === 0 ? (
              'Out of Stock'
            ) : (
              <>
                <ShoppingCart className="w-4 h-4 mr-2" />
                Add to Cart
              </>
            )}
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/ShoppingCart.tsx.content:
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Minus, Plus, Trash2 } from "lucide-react";
import { Link } from "wouter";

interface CartItem {
  id: string;
  productId: string;
  name: string;
  price: string;
  quantity: number;
  imageUrl?: string;
  platform?: string;
}

interface ShoppingCartProps {
  items: CartItem[];
  onUpdateQuantity: (id: string, quantity: number) => void;
  onRemoveItem: (id: string) => void;
}

export function ShoppingCart({ items, onUpdateQuantity, onRemoveItem }: ShoppingCartProps) {
  const subtotal = items.reduce((sum, item) => sum + parseFloat(item.price) * item.quantity, 0);
  const processingFee = 2.99;
  const total = subtotal + processingFee;

  if (items.length === 0) {
    return (
      <div className="h-full flex flex-col">
        <div className="p-6 border-b border-border">
          <h3 className="text-xl font-bold text-card-foreground">Shopping Cart</h3>
        </div>
        <div className="flex-1 flex items-center justify-center">
          <div className="text-center">
            <p className="text-muted-foreground mb-4">Your cart is empty</p>
            <Link href="/" data-testid="link-continue-shopping">
              <Button>Continue Shopping</Button>
            </Link>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col">
      <div className="p-6 border-b border-border">
        <h3 className="text-xl font-bold text-card-foreground">Shopping Cart</h3>
      </div>

      {/* Cart Items */}
      <div className="flex-1 overflow-y-auto p-6">
        <div className="space-y-4">
          {items.map((item) => (
            <div key={item.id} className="flex items-center space-x-4 p-4 bg-muted rounded-lg" data-testid={`cart-item-${item.id}`}>
              {item.imageUrl && (
                <img
                  src={item.imageUrl}
                  alt={item.name}
                  className="w-16 h-16 rounded-lg object-cover"
                  data-testid={`img-cart-item-${item.id}`}
                />
              )}

              <div className="flex-1">
                <h4 className="font-medium text-card-foreground" data-testid={`text-cart-item-name-${item.id}`}>
                  {item.name}
                </h4>
                {item.platform && (
                  <p className="text-sm text-muted-foreground" data-testid={`text-cart-item-platform-${item.id}`}>
                    {item.platform}
                  </p>
                )}

                <div className="flex items-center mt-2">
                  <Button
                    size="icon"
                    variant="outline"
                    className="w-8 h-8"
                    onClick={() => onUpdateQuantity(item.id, item.quantity - 1)}
                    disabled={item.quantity <= 1}
                    data-testid={`button-decrease-${item.id}`}
                  >
                    <Minus className="w-4 h-4" />
                  </Button>
                  <span className="mx-3 font-medium min-w-[2rem] text-center" data-testid={`text-quantity-${item.id}`}>
                    {item.quantity}
                  </span>
                  <Button
                    size="icon"
                    variant="outline"
                    className="w-8 h-8"
                    onClick={() => onUpdateQuantity(item.id, item.quantity + 1)}
                    data-testid={`button-increase-${item.id}`}
                  >
                    <Plus className="w-4 h-4" />
                  </Button>
                </div>
              </div>

              <div className="text-right">
                <p className="font-bold text-accent" data-testid={`text-cart-item-price-${item.id}`}>
                  ${(parseFloat(item.price) * item.quantity).toFixed(2)}
                </p>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => onRemoveItem(item.id)}
                  className="text-destructive hover:text-destructive mt-1 p-0 h-auto"
                  data-testid={`button-remove-${item.id}`}
                >
                  <Trash2 className="w-4 h-4 mr-1" />
                  Remove
                </Button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Cart Footer */}
      <div className="p-6 border-t border-border bg-card">
        <div className="space-y-2 mb-4">
          <div className="flex justify-between">
            <span className="text-muted-foreground">Subtotal:</span>
            <span className="font-medium" data-testid="text-subtotal">
              ${subtotal.toFixed(2)}
            </span>
          </div>
          <div className="flex justify-between">
            <span className="text-muted-foreground">Processing Fee:</span>
            <span className="font-medium" data-testid="text-processing-fee">
              ${processingFee.toFixed(2)}
            </span>
          </div>
          <Separator />
          <div className="flex justify-between items-center">
            <span className="text-lg font-medium text-card-foreground">Total:</span>
            <span className="text-2xl font-bold text-accent" data-testid="text-total">
              ${total.toFixed(2)}
            </span>
          </div>
        </div>

        <Link href="/checkout" data-testid="link-checkout">
          <Button className="w-full" size="lg">
            Proceed to Checkout
          </Button>
        </Link>

        <p className="text-xs text-muted-foreground text-center mt-2">
          Secure checkout with Stripe & crypto payments
        </p>
      </div>
    </div>
  );
}

